<script>

	$(function() {
		$("#checkout-form").validate({
			submitHandler: function(form) {
				
				if ($('#district-valid').val() == 0) {
					alert('District not valid');
					$('#kecamatan').focus();
				} else {
					form.submit();
				}
				
			}
		});
		
