			var qty_max = parseInt($("#cart_quantity_input_"+ID).attr("data-max"));

			if(id_tombol == "qtyplus") {
				if (room_qty >= qty_max) {
					alert('quantity limit exceeded');
				} else {
					room_qty++;
				}
			} else if(room_qty > 1) {
				room_qty--;
			}
			
			$("#cart_quantity_input_"+ID).val(room_qty);
			$("#lblcart_quantity_input_"+ID).html(room_qty);
		});

		
